frappe.listview_settings["Godomall Order"] = {
	onload: function (listview) {
		listview.page.add_menu_item(__("Get Today Order"), function () {
		    var today = new Date();
			var dd = String(today.getDate()).padStart(2, '0');
			var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
			var yyyy = today.getFullYear();

			today = yyyy+'-'+mm+'-'+dd;
			frappe.call({
				method: "godomall_api_customization.api.get_godomall_order?date_type=order&start_date="+today+"&end_date="+today,

